  fileName: string,
  maxDepth: number
): Effect.Effect<string | null, PlatformError, FileSystem.FileSystem | Path.Path> =>
  Effect.gen(function* (_) {
    let current = startDir
    let depth = 0

    while (true) {
      const candidate = path.join(current, fileName)
      const exists = yield* _(fs.exists(candidate))
      if (exists) {
        return candidate
      }

      const parent = path.dirname(current)
      if (parent === current || depth >= maxDepth) {
        return null
      }

      current = parent
      depth += 1
    }
  })

const findSshPrivateKey = (
  fs: FileSystem.FileSystem,
  path: Path.Path,
  cwd: string
): Effect.Effect<string | null, PlatformError, FileSystem.FileSystem | Path.Path> =>
  Effect.gen(function* (_) {
    const envKey = process.env["DOCKER_GIT_SSH_KEY"]?.trim()
    if (envKey && envKey.length > 0) {
      const exists = yield* _(fs.exists(envKey))
      if (exists) {
        return envKey
      }
    }

    const devKey = yield* _(findExistingUpwards(fs, path, cwd, "dev_ssh_key", 6))
    if (devKey !== null) {
      return devKey
    }

    const home = process.env["HOME"]?.trim()
    if (home && home.length > 0) {
      const ed25519 = path.join(home, ".ssh", "id_ed25519")
      const edExists = yield* _(fs.exists(ed25519))
      if (edExists) {
        return ed25519
      }

      const rsa = path.join(home, ".ssh", "id_rsa")
      const rsaExists = yield* _(fs.exists(rsa))
      if (rsaExists) {
        return rsa
      }
    }

    return null
  })

const defaultProjectsRoot = (cwd: string): string => {
  const explicit = process.env["DOCKER_GIT_PROJECTS_ROOT"]?.trim()
  if (explicit && explicit.length > 0) {
    return explicit
  }

  return `${cwd}/.docker-git`
}

const isRepoUrlInput = (input: string): boolean => {
  const trimmed = input.trim().toLowerCase()
  return trimmed.startsWith("http://") ||
    trimmed.startsWith("https://") ||
    trimmed.startsWith("ssh://") ||
    trimmed.startsWith("git@")
}

