  Effect.sync(() => process.argv.slice(2)),
  Effect.map((args) => args.length > 0 && args[0] !== undefined ? { name: args[0] } : {}),
  Effect.flatMap(S.decodeUnknown(cliSchema)),
  Effect.map((input) => toVariant(input))
)
