      - ${config.authorizedKeysPath}:/authorized_keys:ro
      - ${config.codexAuthPath}:${config.codexHome}
      - ${config.codexSharedAuthPath}:${config.codexHome}-shared
      - /var/run/docker.sock:/var/run/docker.sock
    networks:
      - ${networkName}
${maybeBrowserService}

networks:
  ${networkName}:
    driver: bridge

volumes:
  ${config.volumeName}:
