import { Effect } from "effect"

import type { CreateCommand } from "../../core/domain.js"
import { deriveRepoPathParts } from "../../core/domain.js"
import { ensureDockerDaemonAccess } from "../../shell/docker.js"
import type {
  CloneFailedError,
